import React, { Component } from "react";
import { withStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import PropTypes from 'prop-types';
import RecruimentModalTable from './recruiment-modal-table'

class RecrumentModal extends Component {

  render() {
    const { classes, recruments, closeModal, isOpen } = this.props
    return (
      <Modal
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
        open={isOpen}
      >
        <div className={classes.paper}>
          <RecruimentModalTable data={recruments} />
          <Button variant="contained" size="small" color="secondary" className={classes.margin}
            onClick={closeModal.bind(this)} style={{ marginTop: 15 }}>
            Đóng
            </Button>
        </div>
      </Modal>
    );
  }
}

const styles = theme => ({
  paper: {
    position: 'absolute',
    width: 1000,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    top: `50%`,
    left: `50%`,
    transform: `translate(-50%, -50%)`,
    outline: 'none',
  },
});

RecrumentModal.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(RecrumentModal);
